import axios from 'axios';
import {getBooksUrl} from "./AppVariables";
export const login = (userName,password)=>{

  axios.get(getBooksUrl()+"/"+userName,{
    auth: {
    username: userName,
    password: password
    }
  })
   .then(res => {
        console.log(res);
        const books = res.data;
       var allRes = books.length+" adet sonuç bulundu\n\n";
       for(let i=0;i<books.length;i++){
         const book = res.data[i];
         var bookRes = "User :"+book.userName+" Kitap Adı: "+book.bookName
         +" Yazar: "+book.author
         +" Sayfa Sayısı: "+book.pages
         +" Basım Tarihi: "+book.printDate;

         allRes+=bookRes+"\n\n";
       }
       alert(allRes);
  });
}
