}
  handleSubmit(event){
    switch (event.target.name) {
      case "register":
        if(this.state.userName != "null" && this.state.userName != ""){
          if(!this.state.userName.includes(" ")){
            if(this.state.password == this.state.password2){
              if(this.state.password.length >= 5 && this.state.password.length <= 19){
                axios.get(getUsersUrl()+"/"+this.state.userName)
                   .then(res => {
                      const result = res.data;
                      if(!result){
                        alert("kayıt başarılı");
                      }
                      else{
                        alert(this.state.userName+" zaten kayıtlı.Lütfen farklı bir ad deneyiniz.");
                      }
                   })
              }
              else{
                alert("Şifre uzunluğu en az 5 en fazla 19 olmalıdır");
              }
            }
            else{
              alert("Şifreler birbiri ile uyuşmuyor");
            }
          }
          else{
            alert("Kullanıcı adında boşluk karakteri kullanılamaz");
          }
        }
        else{
          alert("Kullanıcı adı boş bırakılamaz");
        }
        break;
      default:
    }
    event.preventDefault();
  }
  render(){
    return(
      <form onSubmit={this.handleSubmit}>
      <ul>
        <li>
          <label html="name">Kullanıcı Adı</label>
          <input type="text" name="userName"  onChange={this.handleChange} />
        </li>
        <li>
          <label html="name">Şifre</label>
          <input type="text" name="password"  onChange={this.handleChange} />
        </li>
        <li>
          <label html="name">Şifre Tekrarı</label>
          <input type="text" name="password2"  onChange={this.handleChange} />
        </li>
        <input type="submit" value="Kaydol" name="register" onClick={this.handleSubmit} />
      </ul>
      </form>
    );
  }
}
export default Register;
