import React from 'react';
import axios from 'axios';
import ReactDOM from 'react-dom';
import "./BooksPage.css"
import ReactTable from "react-table";
import App from "./App";
import {getBooksUrl} from "./AppVariables";

class UserPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {bookNames:[{bookName:"kitap1"},{bookName:"kitap2"}]}

  }
  exitPage(event){
    ReactDOM.render(<App/>, document.getElementById('root'));
  }
  renderData() {
      return this.state.bookNames.map((details, index) => {
         const {id,bookName} = details; //destructuring
         return (
            <option value={bookName}>{bookName}</option>
         )
      })
   }
  render(){
    return(
      <div>
      <select>
        {this.renderData()}
        </select>
      </div>
    );
  }
}
export default UserPage;
