import React from 'react';
import axios from 'axios';
import "./UserPage.css"
import ReactTable from "react-table";

class UserPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {userName:props.userName,password:props.password};
    this.data = props.data;
  }
  renderTableData() {
      return this.data.map((details, index) => {
         const { id, bookName, author,pages,printDate } = details; //destructuring
         return (
            <tr key={id}>
                <td><input type="checkbox" value="Giriş" name="login"/></td>
               <td>{bookName}</td>
               <td>{author}</td>
               <td>{pages}</td>
               <td>{printDate.slice(0,10)}</td>
            </tr>
         )
      })
   }
  render(){
    return(
      <div>
      <table id='books'>
      <tbody>
      <tr>
        <th>   </th>
        <th>Kitap Adı</th>
        <th>Yazar</th>
        <th>Sayfa Sayısı</th>
        <th>Basım Tarihi</th>
      </tr>
      {this.renderTableData()}
      </tbody>
      </table>
      </div>
    );
  }
}
export default UserPage;
