import React from 'react';
//import axios from 'axios';
import ReactDOM from 'react-dom';
import "./BooksPage.css"
import App from "./App";
//import {getBooksUrl} from "./AppVariables";

class UserPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {books:props.books,selectedId:"0"};

    this.changedSelect = this.changedSelect.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.findBook = this.findBook.bind(this);
    }
  exitPage(event){
    ReactDOM.render(<App/>, document.getElementById('root'));
  }
  renderData() {
      return this.state.books.map((details, index) => {
         const {id,bookName} = details;
         return (
            <option key={id} value={id}>{bookName}</option>
         )
      })
   }
   findBook(book){
    return book.id === this.state.selectedId;
   }
   changedSelect(event){
     this.setState({selectedId : event.target.value});
   }
   handleSubmit(event){
     switch (event.target.name) {
       case "add":
       alert(this.state.books.find(this.findBook).id);
         break;
        case "details":
        var res = "";
        for(let i=0;i<this.state.books.length;i++){
          if(this.state.books[i].id == this.state.selectedId){
            res+="Kitap Adı: "+this.state.books[i].bookName+" ";
            res+="Yazar: "+ this.state.books[i].author+" ";
            res+="Sayfa Sayısı: "+ this.state.books[i].pages+" \n";
          }
        }
        alert(res);
          break;
       default:
     }
   }
  render(){
    return(
      <div>
        <div>
          <select onChange={this.changedSelect}>
          <option value="0" >Kitap Seçiniz</option>
            {this.renderData()}
          </select>
          <input type="submit" value="Detay" name="details" onClick={this.handleSubmit} />
          <input type="submit" value="Ekle" name="add" onClick={this.handleSubmit} />
          </div>
      <div>

      </div>
      </div>
    );
  }
}
export default UserPage;
