import React from 'react';
import axios from 'axios';
import "./App.css";
import ReactDOM from 'react-dom';
import Register from './Register';
import BooksPage from "./BooksPage";
import UserPage from "./UserPage";
import {getLoginUrl} from "./AppVariables";
import {getBooksUrl} from "./AppVariables";
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {userName:"",password:""};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event){
    this.setState({[event.target.name]: event.target.value});
  }
  login(userName,password){
    axios({
      method: "get",
      url: getLoginUrl(),
      auth: {
      username: userName,
      password: password
    }
    })
    .then(res => {
      if(res.data){
        axios({
          method: "get",
          url: getBooksUrl(),
          auth: {
          username: userName,
          password: password
        }
        })
        .then(res => {
          ReactDOM.render(<BooksPage data={res.data} userName={userName} password={password}/>, document.getElementById('root'));
       })
        .catch(error => {
          alert("Bağlantı hatası ")
       });
      }
      else{
        axios({
          method: "get",
          url: getBooksUrl(),
          auth: {
          username: userName,
          password: password
        }
        })
        .then(res => {
          console.log(res);
          var books = res.data;

          axios({
            method: "get",
            url: getUserBooksUrl()+"/"+userName,
            auth: {
            username: userName,
            password: password
          }
          })
          .then(res => {
            console.log(res);
            var userbooks = res.data;
            ReactDOM.render(<UserPage userbooks={userbooks} books={books}userName={userName} password={password}/>, document.getElementById('root'));
         })
          .catch(error => {
            alert("Bağlantı hatası userbooks")
         });
       })
        .catch(error => {
          alert("Bağlantı hatası books")
       });
      }
   })
    .catch(error => {
      alert("Kullanıcı adı veya şifre yanlış.");
   });
  }
  handleSubmit(event){
    switch (event.target.name){
      case "login":
        this.login(this.state.userName,this.state.password);
        break;
      case "register":
      ReactDOM.render(<Register/>, document.getElementById('root'));
        break;
      default:
    }
    event.preventDefault();
  }
  render(){
    return(
      <form className="form-style-7" onSubmit={this.handleSubmit}>
      <ul>
        <li><span>Giriş</span></li>
        <li>
          <label html="name">Kullanıcı Adı</label>
          <input type="text" name="userName"  onChange={this.handleChange} />
        </li>
        <li>
          <label html="name">Şifre</label>
          <input type="password" name="password"  onChange={this.handleChange} />
        </li>
        <input type="submit" value="Giriş" name="login" onClick={this.handleSubmit} />
        <input type="submit" value="Kaydol" name="register" onClick={this.handleSubmit} />
      </ul>
      </form>
    );
  }
}
export default App;
