import React from 'react';
import axios from 'axios';
import ReactDOM from 'react-dom';
import "./BooksPage.css"
import App from "./App";
import {getUserBooksUrl} from "./AppVariables";
import {getUsersUrl} from "./AppVariables";

class UserPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {books:props.books,userbooks:props.userbooks,electedId:"0",author:"",pages:0};
    this.counter = 0;

    this.changedSelect = this.changedSelect.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.exitPage = this.exitPage.bind(this);
    this.deleteSubmit = this.deleteSubmit.bind(this);
    }
  exitPage(event){
    ReactDOM.render(<App/>, document.getElementById('root'));
  }
  reflesh(userName,password){
    axios({
      method: "get",
      url: getUserBooksUrl()+"/"+this.props.userId,
      auth: {
      username: userName,
      password: password
    }
    })
    .then(res => {
      this.setState({userbooks:res.data});
   })
    .catch(error => {
      alert("Bağlantı hatası userbooks reflesh")
   });
  }
  renderData() {
      return this.state.books.map((details, index) => {
         const {id,bookName} = details;
         return (
            <option key={id} value={id}>{bookName}</option>
         )
      })
   }
   add(userId,bookId){
     axios({
       method: "post",
       url: getUserBooksUrl(),
       data: {
         userId : userId,
         bookId : bookId
       },
       auth: {
       username: this.props.userName,
       password: this.props.password
     }
     })
     .then(res => {
       this.reflesh(this.props.userName,this.props.password);
    })
     .catch(error => {
       alert("Ekleme başarısız")
    });
   }
   changedSelect(event){
     this.counter++;
     this.setState({selectedId : event.target.value});
     if(this.counter > 1){
       var book = this.state.books.find(books => books.id == event.target.value);
       this.setState({author : book.author});
       this.setState({pages : book.pages});
     }
     else{
       event.target.remove(0);
       var authorDoc = document.getElementById("author");
       var  pagesDoc = document.getElementById("pages");
       authorDoc.style.visibility = "visible";
       pagesDoc.style.visibility = "visible";
       var book = this.state.books.find(books => books.id == event.target.value);
       this.setState({author : book.author});
       this.setState({pages : book.pages});
     }
   }
   handleSubmit(event){
     switch (event.target.name) {
       case "add":
       this.add(this.props.userId,this.state.selectedId);
         break;
       default:
     }
   }
     deleteSubmit(event){
     const Id = event.target.name;
     axios({
       method: "delete",
       url: getUserBooksUrl()+"/"+Id,
       auth: {
       username: this.props.userName,
       password: this.props.password
     }
     })
     .then(res => {
       this.reflesh(this.props.userName,this.props.password);
     })
     .catch(error => {
       alert("Silme başarısız Id: "+Id+" bulunmadı");
     });   }
   renderTable(){
       return this.state.userbooks.map((details, index) => {
          const { id,bookId,createdAt } = details; //destructuring
          const x = 10;
          return (
             <tr key={id}>
                <td>{this.state.books.find(books => books.id == bookId).bookName}</td>
                <td>{new Date(createdAt).getFullYear()}</td>
                <td><input type="submit" value="X" name={id} onClick={this.deleteSubmit}/></td>
             </tr>
          )
       })
    }
  render(){
    return(
      <div>
        <div className="box">
          <select onChange={this.changedSelect}>
          <option value="0">Kitap Seçiniz</option>
            {this.renderData()}
          </select>
          <input type="submit" value="+" name="add" onClick={this.handleSubmit} />
          <input type="button" value="Çık" name="exit" onClick={this.exitPage} />
          <p type="userName">{this.props.userName}</p>
          <p id="author" type="author">Yazar:{this.state.author}</p>
          <p id="pages" type="pages">Sayfa Sayısı:{this.state.pages}</p>
          </div>
      <div>
      </div>
      <table id="books">
      <thead>
    <tr>
      <th>Kitap Adı</th>
      <th>Tarih</th>
      <th>Düzenle</th>
    </tr>
    </thead>
      <tbody>
        {this.renderTable()}
        </tbody>
    </table>
      </div>
    );
  }
}
export default UserPage;
