import React from 'react';

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {id:0,bookName:"",author:"",pages:0,printDate:""};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event){
    this.setState({[event.target.name]: event.target.value});
  }
  handleSubmit(event){
    switch (event.target.name){
      case "add":
        add(this.state.bookName,this.state.author,this.state.pages,this.state.printDate);
        break;
      case "update":
        update(this.state.id,this.state.bookName,this.state.author,this.state.pages,this.state.printDate);
        break;
      case "delete":
        del(this.state.id);
        break;
      case "getAll":
        getAll();
        break;
      case "getById":
        getById(this.state.id);
         break;
      default:
    }
    event.preventDefault();
  }
  render(){
    return(
      <form onSubmit={this.handleSubmit}>
      <ul>
        <li>
          <label html="name">Id</label>
          <input type="number" name="id" onChange={this.handleChange} />
        </li>
        <li>
          <label html="name">Kitap Adı</label>
          <input type="text" name="bookName"  onChange={this.handleChange} />
        </li>
        <li>
          <label html="name">Yazar</label>
          <input type="text" name="author"  onChange={this.handleChange} />
        </li>
        <li>
          <label html="name">Sayfa Sayısı</label>
          <input type="number" name="pages"  onChange={this.handleChange} />
        </li>
        <li>
          <label html="name">Basım Tarihi</label>
          <input type="date" name="printDate"  onChange={this.handleChange} />
        </li>
        <input type="submit" value="Ekle" name="add" onClick={this.handleSubmit} />
        <input type="submit" value="Güncelle" name="update" onClick={this.handleSubmit} />
        <input type="submit" value="Sil" name="delete" onClick={this.handleSubmit} />
        <input type="submit" value="Listele" name="getAll" onClick={this.handleSubmit} />
        <input type="submit" value="Bul" name="getById" onClick={this.handleSubmit} />
      </ul>
      </form>
    );
  }
}
export default Login;
