import React from 'react';
import axios from 'axios';
import "./UserPage.css"
import ReactTable from "react-table";
import {add} from "./Post";
import {getBooksUrl} from "./AppVariables";
import {del} from "./Delete";

class UserPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {books:props.data,bookName:"",author:"",pages:0,printDate:""}
    this.handleChange = this.handleChange.bind(this);
    this.addSubmit = this.addSubmit.bind(this);
    this.editSubmit = this.editSubmit.bind(this);
    this.deleteSubmit = this.deleteSubmit.bind(this);
  }
  reflesh(userName,password){
    axios.get(getBooksUrl()+"/"+userName,{
      auth: {
      username: userName,
      password: password
      }
    })
     .then(res => {
       this.setState({books:res.data});
       console.log(res.data);
    })
    .catch(error => {
        alert("Kullanıcı adı veya şifre yanlış.");
    })
  }
  addSubmit(event){
    add(this.props.userName,this.props.password,this.state.bookName
    ,this.state.author,this.state.pages,this.state.printDate);

    this.reflesh(this.props.userName,this.props.password);
  }
  editSubmit(event){
    alert(event.target.name);
  }
  deleteSubmit(event){
    /*
    axios.delete(getBooksUrl()+"/"+event.target.name,{
      auth: {
      username: this.props.userName,
      password: this.props.password
    }
    })
        .then(res => {
          alert("Id: "+event.target.name+" Silindi");
          this.reflesh(this.props.userName,this.props.password);
        })
        .catch(error => {
          alert("Silme başarısız Id: "+event.target.name+" bulunmadı");
        });
        */
        alert(event.target.name);
  }
  handleChange(event){
    this.setState({[event.target.name]: event.target.value});
    //this.setState({bookName: event.target.value});
  }
  renderTableData() {
      return this.state.books.map((details, index) => {
         const { id, bookName, author,pages,printDate } = details; //destructuring
         return (
            <tr key={id}>
            <td><input type="submit" value="Sil" name={id} onClick={this.deleteSubmit}/></td>
               <td>{bookName}</td>
               <td>{author}</td>
               <td>{pages}</td>
               <td>{printDate.slice(0,10)}</td>
               <td><input type="submit" value="Düzenle" name={id} onClick={this.editSubmit}/></td>
            </tr>
         )
      })
   }
  render(){
    return(
      <div>
        <table id="books">
        <thead>
      <tr>
        <th></th>
        <th>Kitap Adı</th>
        <th>Yazar</th>
        <th>Sayfa Sayısı</th>
        <th>Basım Tarihi</th>
        <th>Düzenle</th>
      </tr>
      </thead>
        <tbody>
          <tr>
          <td></td>
          <td><input type="text" name="bookName"  onChange={this.handleChange} /></td>
          <td><input type="text" name="author"  onChange={this.handleChange} /></td>
          <td><input type="number" name="pages"  onChange={this.handleChange} /></td>
          <td><input type="date" name="printDate"  onChange={this.handleChange} /></td>
          <td><input type="submit" value="Ekle" name="add" onClick={this.addSubmit} /></td>
          </tr>
          {this.renderTableData()}
          </tbody>
      </table>
    </div>
    );
  }
}
export default UserPage;
