import React from 'react';
import axios from 'axios';
import "./UserPage.css"
import ReactTable from "react-table";

class UserPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {userName:props.userName,password:props.password};
    this.data = props.data;
  }
  addSubmit(event){
    alert(event.target.name);
  }
  editSubmit(event){
    alert(event.target.name);
  }
  deleteSubmit(event){
    alert(event.target.name);
  }
  renderTableData() {
      return this.data.map((details, index) => {
         const { id, bookName, author,pages,printDate } = details; //destructuring
         return (
            <tr key={id}>
              <td><input type="submit" value="Sil" name={id} onClick={this.deleteSubmit}/></td>
               <td contenteditable="true">{bookName}</td>
               <td contenteditable="true">{author}</td>
               <td contenteditable="true">{pages}</td>
               <td contenteditable="true">{printDate.slice(0,10)}</td>
               <input type="submit" value="Düzenle" name={id} onClick={this.editSubmit} />
            </tr>
         )
      })
   }
  render(){
    return(
      <div>
      <table id='books'>
      <tbody>
      <tr>
        <th></th>
        <th>Kitap Adı</th>
        <th>Yazar</th>
        <th>Sayfa Sayısı</th>
        <th>Basım Tarihi</th>
        <th>Düzenle</th>
      </tr>
      <td></td>
       <td contenteditable="true"></td>
       <td contenteditable="true"></td>
       <td contenteditable="true"></td>
       <td contenteditable="true"></td>
       <input type="submit" value="Ekle" name="add" onClick={this.addSubmit} />
      {this.renderTableData()}
      </tbody>
      </table>
      </div>
    );
  }
}
export default UserPage;
