import React from 'react';
import axios from 'axios';
import "./UserPage.css"
import ReactTable from "react-table";

class UserPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {books:props.data};
  }
  addSubmit(event){
    //alert(event.target.name);
    alert(this.state.books[0].bookName);
  }
  editSubmit(event){
    alert(event.target.name);
  }
  deleteSubmit(event){
    alert(event.target.name);
  }
  handleChange(event){
    event.target.value = "new value";
  }
  renderTableData() {
      return this.state.books.map((details, index) => {
         const { id, bookName, author,pages,printDate } = details; //destructuring
         return (
            <tr key={id}>
              <td><input type="submit" value="Sil" name={id} onClick={this.deleteSubmit}/></td>
               <td><input type="text" value={bookName}/></td>
               <td><input type="text" value={author}/></td>
               <td><input type="number" value={pages}/></td>
               <td><input type="date" value={printDate.slice(0,10)}/></td>
               <input type="submit" value="Düzenle" name={id} onClick={this.editSubmit} />
            </tr>
         )
      })
   }
  render(){
    return(
      <div>
      <table id='books'>
      <thead>
      <tr>
        <th></th>
        <th>Kitap Adı</th>
        <th>Yazar</th>
        <th>Sayfa Sayısı</th>
        <th>Basım Tarihi</th>
        <th>Düzenle</th>
      </tr>
      </thead>
      <tbody>
      <td></td>
       <td><input type="text"/></td>
       <td><input type="text"/></td>
       <td><input type="text"/></td>
       <td><input type="text"/></td>
       <td><input type="submit" value="Ekle" name="add" onClick={this.addSubmit} /></td>
      {this.renderTableData()}
      </tbody>
      </table>
      </div>
    );
  }
}
export default UserPage;
