import axios from 'axios';
import {getBooksUrl} from "./AppVariables";
export const login = (userName,password)=>{

  axios.get(getBooksUrl()+"/"+userName,{
    auth: {
    username: userName,
    password: password
    }
  })
   .then(res => {
        console.log(res);
        const book = res.data;
        var bookRes = "User :"+book.userName+" Kitap Adı: "+book.bookName
        +" Yazar: "+book.author
        +" Sayfa Sayısı: "+book.pages
        +" Basım Tarihi: "+book.printDate;
        alert(bookRes);
  });
}
