import axios from 'axios';
import {getBooksUrl} from "./AppVariables";
export const add = (userName,password,bookName,author,pages,printDate)=>{
   axios.post(getBooksUrl(), {
     auth: {
     username: userName,
     password: password
   },
     userName:userName,
     bookName: bookName,
     author: author,
     pages: pages,
     printDate: printDate })
     .then(res => {
       var bookRes = "Kitap Adı: "+bookName
       +" Yazar: "+author
       +" Sayfa Sayısı: "+pages
       +" Basım Tarihi: "+printDate;
       alert("Ekleme başarılı\n"+bookRes);
    })
     .catch(error => {
       alert("Ekleme başarısız")
    });
}
