import React from 'react';
import axios from 'axios';
//import "./UserPage.css"
import ReactTable from "react-table";

class UserPage extends React.Component {
  constructor(props) {
    super(props);
    this.data = props.data;
  }
  addSubmit(event){
    //alert(event.target.name);
    console.log(this.state.books);
  }
  editSubmit(event){
    alert(event.target.name);
  }
  deleteSubmit(event){
    alert(event.target.name);
  }
  handleChange(event){
    event.target.value = "new value";
  }
  renderTableData() {
      return this.data.map((details, index) => {
         const { id, bookName, author,pages,printDate } = details; //destructuring
         return (
            <tr key={id}>
               <td>{bookName}</td>
               <td>{author}</td>
               <td>{pages}</td>
               <td>{printDate.slice(0,10)}</td>
            </tr>
         )
      })
   }
  render(){
    return(
      <table>
      /*
      <thead>
      <tr>
        <th></th>
        <th>Kitap Adı</th>
        <th>Yazar</th>
        <th>Sayfa Sayısı</th>
        <th>Basım Tarihi</th>
        <th>Düzenle</th>
      </tr>
      </thead>
      */
      <tbody>
      /*
      <td></td>
       <td><input type="text"/></td>
       <td><input type="text"/></td>
       <td><input type="text"/></td>
       <td><input type="text"/></td>
       <td><input type="submit" value="Ekle" name="add" onClick={this.addSubmit} /></td>
       */
      {this.renderTableData()}
      </tbody>
      </table>
    );
  }
}
export default UserPage;
