import React from 'react';
import axios from 'axios';
import "./App.css";
import ReactDOM from 'react-dom';
import Register from './Register';
import BooksPage from "./UserPage";
import {getLoginUrl} from "./AppVariables";
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {userName:"",password:""};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event){
    this.setState({[event.target.name]: event.target.value});
  }
  login(userName,password){
    axios({
      method: "get",
      url: getLoginUrl(),
      auth: {
      username: userName,
      password: password
    }
    })
    .then(res => {
      alert(res);
      if(res){
        alert("admin giriş yaptı");
        //ReactDOM.render(<BooksPage userName={userName} password={password}/>, document.getElementById('root'));
      }
      else{
        alert("kullanıcı giriş yaptı");
      }
      //ReactDOM.render(<UserPage data={res.data} userName={userName} password={password}/>, document.getElementById('root'));
   })
    .catch(error => {
      alert("Kullanıcı adı veya şifre yanlış.");
   });
  }
  handleSubmit(event){
    switch (event.target.name){
      case "login":
        this.login(this.state.userName,this.state.password);
        break;
      case "register":
      ReactDOM.render(<Register/>, document.getElementById('root'));
        break;
      default:
    }
    event.preventDefault();
  }
  render(){
    return(
      <form className="form-style-7" onSubmit={this.handleSubmit}>
      <ul>
        <li><span>Giriş</span></li>
        <li>
          <label html="name">Kullanıcı Adı</label>
          <input type="text" name="userName"  onChange={this.handleChange} />
        </li>
        <li>
          <label html="name">Şifre</label>
          <input type="password" name="password"  onChange={this.handleChange} />
        </li>
        <input type="submit" value="Giriş" name="login" onClick={this.handleSubmit} />
        <input type="submit" value="Kaydol" name="register" onClick={this.handleSubmit} />
      </ul>
      </form>
    );
  }
}
export default App;
